import React, { useCallback, useEffect, useState } from "react";
import "./content.css";
import { BsFillArrowRightCircleFill } from "react-icons/bs";
import { client } from "../../client";

function Content() {
  const [page, setPage] = useState([]);

  const getPage = useCallback(async () => {
    try {
      const response = await client.getEntries({ content_type: "landingPage" });
      const responseData = response.items;
      if (responseData) setPage(responseData);
    } catch (err) {
      console.error(err);
    }
  });
  useEffect(() => {
    getPage();
  }, []);

  return (
    <>
      <div className="content_section">
        <div className="content__title">
          <h1>
            Creative Digital <span style={{ color: "tomato" }}>agency</span>
            <br />
            for mindful brands :)
          </h1>
          <h2>{page?.name}</h2>
        </div>

        <div className="content_sub_title">
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Aspernatur
            <br />
            alias blanditiis
          </p>
        </div>

        <div className="start_project_content__btn">
          <button className="start__project__btn">
            Start a project &nbsp;
            <div className="btn_content_icon">
              <BsFillArrowRightCircleFill size={25} />
            </div>
          </button>
        </div>
      </div>
    </>
  );
}

export default Content;
